void Map::Init() {
	m_tileMap.assign(mapSize, std::vector <Tile*>(mapSize));
    for (int i = 0; i < m_mapID.size(); ++i) {
        for (int j = 0; j < m_mapID.size(); ++j) {
            if (m_mapID[i][j] != 0) {
                m_tileMap[i][j] = new Tile(m_mapID[i][j], sf::Vector2i(i, j));
                m_tileMap[i][j]->Init();
                if (m_mapID[i][j] != 5) m_listWall->push_back(m_tileMap[i][j]);
            }
        }
    }
}

void Map::Update(float deltaTime) {
}

void Map::Render(sf::RenderWindow* window) {
	for (int i = 0; i < m_mapID.size(); ++i) {
		for (int j = 0; j < m_mapID.size(); ++j) {
            if (m_mapID[i][j] != 0) {
                m_tileMap[i][j]->Render(window);
            }
		}
	}
}

sf::Vector2f Map::GetMapSize() {
    return sf::Vector2f(tileSize * mapSize * Tile::m_scale.x, tileSize * mapSize * Tile::m_scale.y);
}

std::list<Tile*>* Map::GetWall() {
    return m_listWall;
}
